                        }
                        Accumulative = AcumelativeListFromEntry.Select(x => x.Amount).Sum();

                    }
                    else if (account.Haveitem)
                    {
                        var Request = LoadChildAccountForIncomeStatment(ALLAccountsListDB, account.Id, Year, Month, Day);
